import React, { useState, useContext} from 'react';
import RootContext from "../../providers/root";
import './home-page.css';
import ClassroomIMG from "./classroom-stock.jpg"
import { RecordingMenu, Progress, History, Settings, Loading } from '../../components';

function HomePage() {
    const {
        setCurrentPage
    } = useContext(RootContext);

    const [menuSlide, setMenuSlide] = useState();

    const renderComponent = () => {
        switch (menuSlide) {
            case 'progress':
                return <Progress />;
            case 'history':
                return <History />;
            case 'settings':
                return <Settings />;
            case 'loading':
                return <Loading />
            default:
                return <RecordingMenu setMenuSlide={setMenuSlide} />;
        }
    };

    return (
        <div className='home'>
            
        </div>
    );
}

export default HomePage;
