import { useState } from 'react';
import './home-page.css';
import axios from "axios";

function HomePage() {
const [recording, isRecording] = useState(false)
const [recordingSaved, isRecordingSaved] = useState(false)
const [audioPath, setAudioPath] = useState(false)

    const startRecording = async () => {
        try {
          const response = await axios.get("http://localhost:5000/api/start_recording");
          console.log(response)
          isRecording(true)
          isRecordingSaved(false)
        } catch (error) {
          console.error("Error fetching insect types:", error);
        }
    }

    const stopRecording = async () => {
        try {
          const response = await axios.get("http://localhost:5000/api/stop_recording");
          setAudioPath(response)
          isRecording(false)
          isRecordingSaved(true)
          } catch (error) {
          console.error("Error fetching insect types:", error);
        }
    }

    return (
        <div>
            <h1>Recording Page</h1>
            <button onClick={() => startRecording()}>Start</button>
            <button onClick={() => stopRecording()}>Stop</button>

            {recording && 
                <p>Recording...</p>
            }
            {recordingSaved && 
                <p>Recording Saved at {audioPath}</p>
            }

        </div>
    );
}

export default HomePage;
